if (!Objects) { var Objects = []; }
function TabSet(outerElmId, style) {
	var a = arguments;
	this.style = (style == null) ? '' : '_' + style;
	this._outerElmId = outerElmId;
	this._id;
	this._objectId;
	this.tabs = new Array;
	this._activeTabIdx = 0;
	this._onTabSelectEvent
	this._constructor = function() {
		this._id = Objects.length;
		Objects[this._id] = this;
		this._objectId = "TabSet_" + this._id;
	}
	this.addTab = function(caption,container) {
		if (typeof(caption) == 'object') {
			var o = caption;
		} else {
			var o = new Object;
			o.caption   = caption;
			o.container = container;
		}
		o.tabIdx = this.tabs.length;
		this.tabs[o.tabIdx] = o;
	}
	this.render = function() {
		var ret = new Array;
		ret[ret.length] = '<div class="tabsetTabsDiv' + this.style + '">';
		//next line add "float:left;"  modify tangzhi@2007-11-4 15:37
		ret[ret.length] = '<div style="width:2px;min-width:2px;display:inline;"></div>';
		//ret[ret.length] = '<div style="width:4px;min-width:2px;display:inline;float:left;"></div>';
		//ret[ret.length] = '<div id="' + this._outerElmId + 'gomove" style="float:right;display:inline;z-index:9998;"><a href="javascript:void(0)" class="tabgo"><img src="component/images/tabgoto.gif" alt="更多..." width="12" height="20" border="0"/></a></div>';
		for (var i=0; i<this.tabs.length; i++) {
			if (i == this._activeTabIdx) {
				var cls = 'TabsetActive' + this.style;
			} else {
				var cls = 'TabsetInactive' + this.style;
				if (this.tabs[i].container) this.tabs[i].container.style.display = 'none';
			}
			ret[ret.length] = '<div unselectable="On" id="' + this._objectId + '_tabCap_' + i + '" class="Tabset' + this.style + ' ' + cls + '" style="display:inline;" onclick="Objects['+this._id+'].switchTo(' + i + ');"><span class="left"></span><span class="text">' + this.tabs[i].caption + '</span><span class="right"></span></div>';
		}
		ret[ret.length] = '</div>';
		return ret.join('');
	}
	this.draw = function() {
		var elem = document.getElementById(this._outerElmId + '_tabs');
		elem.className = "TabsetTabs" + this.style;
		if (elem) elem.innerHTML = this.render();
	}
	this.switchTo = function(theReg) {
		newRegIdx = -1;
		if (theReg=='') theReg = '0';
		if (isNaN(parseInt(theReg))) {
			for (var i=0; i<this.tabs.length; i++) {
				if (this.tabs[i].caption == theReg) (newRegIdx = i);
			}
		} else {
			newRegIdx = theReg;
		}
		if (newRegIdx<0) return;
		for (var i=0; i<this.tabs.length; i++) {
			var elem = document.getElementById(this._objectId + '_tabCap_' + i);
			if (!elem) continue;
			if (newRegIdx == i) {
				this._activeTabIdx = i;
				elem.className = 'Tabset' + this.style + ' TabsetActive' + this.style;
				this.tabs[i].container.style.display = 'block';
				if (typeof(this.tabs[i].onFocus) != 'undefined') {
					this._triggerFunction(this.tabs[i].onFocus);
				}
				this.fireOnTabSelect();
			} else {
				elem.className = 'Tabset' + this.style + ' TabsetInactive' + this.style;
				this.tabs[i].container.style.display = 'none';
				if (typeof(this.tabs[i].onBlur) != 'undefined') {
					this._triggerFunction(this.tabs[i].onBlur);
				}
			}
		}
	}
	this.getActiveTab = function() {
		return this.tabs[this._activeTabIdx];
	}
	this.onTabSelect = function(yourEvent) {
		this._onTabSelectEvent = yourEvent;
	}
	this.fireOnTabSelect = function() {
		if (this._onTabSelectEvent) {
			func = this._onTabSelectEvent;
			if (this._onTabSelectEvent == 'string') {
				eval(func);
			} else {
				func(this);
			}
		}
		return true;
	}
	this._triggerFunction = function(func) {
		if (typeof(func) == 'function') {
			func();
		} else if (typeof(func) == 'string') {
			eval(func);
		}
	}
	this._constructor();
}

/** table edit */
function TableEdit(tableName, encode) {
	this.tableName = tableName;
	this.table = getElement(tableName);
	this.header = this.table.rows[0];
	this.rowIndex = 0;
	this.isSelected = false;
	this.encode = encode == null ? false : encode;
	this.isChanged = false;
	
	/** inner text */
	this.setCellValue = function(hcell, cell, content) {
		cell.id = hcell.id;
		cell.style.display = hcell.style.display;
		content = this.encodeCellValue(content);
		cell.innerHTML = content;
	}
	/** encode content */
	this.encodeCellValue = function(content) {
		if (this.encode && content != null) {
			content = content.replace("&", "&amp;");
			content = content.replace("<", "&lt;");
			content = content.replace(">", "&gt;");
		}
		return content;
	}
	/** decode content */
	this.decodeCellValue = function(content) {
		if (this.encode && content != null) {
			content = content.replace("&lt;", "<");
			content = content.replace("&gt;", ">");
			content = content.replace("&amp;", "&");
		}
		return content;
	}
	/** get cell */
	this.getCell = function(row, cellName) {
		var hcells = this.header.cells;
		for (var i=0; i<hcells.length; i++) {
			var cell = row.cells[i];
			if (hcells[i].id == "col_" + cellName) return cell;
		}
		return null;
	}
	/** trigger cell */
	this.triggerCell = function(cellName, cellValue) {
		var row = getElementByTag(window.event.srcElement, "tr");
		var cell = this.getCell(row, cellName);
		var x_tag = this.getCell(row, "X_TAG");
	
		cell.innerHTML = cellValue;
		if (x_tag.innerHTML == "") {
			x_tag.innerHTML = "2";
		}
	}
	/**modify by hujia 20070808*/
	/** verify table */
	this.verifyTable = function(changed) {
		/*changed=true 表示校验表格数据是否修改过*/
		if (changed != null && changed) {
			if (!this.isChanged) {
				alert('表格数据没有更新！');
				return false;
			}else {
				return true;
			}
		}else {
			if (!this.isSelected) {
				alert('请选择表格！');
				return false;
			}
		}
		return true;
	}
	/**modify by hujia 20070808*/
	/** check row */
	/** isupdate=true 表示修改时判断，key:修改时判断主键不能被修改  */
	this.checkRow = function(cellName,isupdate,key) {
		var cellNames = cellName.split(",");
		var keys = key != null ? key.split(",") : "";
		
		for (var i=1; i<this.table.rows.length; i++) {
			var row = this.table.rows[i];
			if (row.style.display == "none") continue;
			
			for (var k=0; k<this.header.cells.length; k++) {
				if (isupdate != null && isupdate && i == this.rowIndex) {
					for (var n=0; n<keys.length; n++) {				
						if(keys[n] == this.header.cells[k].id.substring("col_".length)) {
							var fieldValue = getElementValue(keys[n]);
							if(fieldValue != this.decodeCellValue(trim(row.cells[k].innerHTML))){
								var error = this.header.cells[k].innerHTML+"是主键不能修改！";
								alert(error);
								return false;
							}
						}						
			  		}
			  		continue;					
				}
				
				for (var j=0; j<cellNames.length; j++) { 
    				var fieldValue = getElementValue(cellNames[j]);
    				var info = this.getCell(this.header, cellNames[j]).innerText + "已经存在！";
					if (this.getCell(row, cellNames[j]).innerText == fieldValue) {
						alert(info);
						return false;
					}
				}
			}
		}
		return true;
	}
	/** insert row */
	this.insertRow = function() {
		var row = this.table.insertRow(this.table.rows.length);
	
		var hcells = this.header.cells;
		for (var i=0; i<hcells.length; i++) {
			if (hcells[i].id == null || !startsWith(hcells[i].id, "col_")) continue;

			var cell = row.insertCell(i);
			var cellName = hcells[i].id.substring("col_".length);
			var field = getElement(cellName);
		
			if (cellName == "X_TAG") {
				this.setCellValue(hcells[i], cell, "0");
			} else {
				if (field != null) {
					this.setCellValue(hcells[i], cell, field.value);
					field.value = '';
				} else {
					this.setCellValue(hcells[i], cell, '');
				}
			}
		}
	}
	/** update row */
	this.updateRow = function() {
		var row = this.table.rows[this.rowIndex];
	
		var hcells = this.header.cells;
		for (var i=0; i<hcells.length; i++) {
			if (hcells[i].id == null || !startsWith(hcells[i].id, "col_")) continue;

			var cell = row.cells[i];
			var cellName = hcells[i].id.substring("col_".length);
			var field = getElement(cellName);
		
			if (cellName == "X_TAG" && cell.innerText == "") {
				this.setCellValue(hcells[i], cell, "2");
			} else {
				if (field != null) {
					this.setCellValue(hcells[i], cell, field.value);
					field.value = '';
				}
			}
		}
	}
	/** del row */
	this.deleteRow = function() {
		var row = this.table.rows[this.rowIndex];
	
		var isDelete = true;
		var hcells = this.header.cells;
		for (var i=0; i<hcells.length; i++) {
			if (hcells[i].id == null || !startsWith(hcells[i].id, "col_")) continue;

			var cell = row.cells[i];
			var cellName = hcells[i].id.substring("col_".length);
			var field = getElement(cellName);

			if (cellName == "X_TAG") {
				if (cell.innerText == "" || cell.innerText == "2") {
					this.setCellValue(hcells[i], cell, "1");
					isDelete = false;
				}
			} else {
				if (field != null) {
					field.value = '';
				}
			}
		}

		if (isDelete) {
			this.table.deleteRow(this.rowIndex);
		} else {
			row.style.display = "none";
		}
		this.isSelected = false;
	}
	/** click row */
	this.clickRow = function() {
		for (var i=1; i<this.table.rows.length; i++) {
			this.table.rows[i].className = "row_even";
		}

		var row = getElementByTag(window.event.srcElement, "tr");
		if (row == null || row.rowIndex == 0) return;

		row.className = "row_select";
		this.rowIndex = row.rowIndex;
		this.isSelected = true;

		for (var i=0; i<this.header.cells.length; i++) {
			var cellName = this.header.cells[i].id.substring("col_".length);
			var field = getElement(cellName);
			if (field != null) {
				var fieldValue = this.decodeCellValue(trim(row.cells[i].innerHTML));
				if (field.tagName == "INPUT" && field.type != null && (field.type.toUpperCase() == "CHECKBOX" || field.type.toUpperCase() == "RADIO")) {
					field.checked = fieldValue == field.value;
				} else {
					field.value = fieldValue;
				}
			}
		}
	}
	/** get cols value */
	this.getColsValue = function(cols, flag) {
		var str = "";
		
		var rows = this.table.rows;
		for (var i=1; i<rows.length; i++) {
			var cell = this.getCell(rows[i], cols);
			if (cell == null) alert("column " + cols + " not found");
			str = (flag == true) ? str + cell.innerText + "," : str + cell.innerText;
		}
		
		return str;
	}
	/** encode value */
	this.encodeValue = function(num, key, cols, def, flag) {
		var str = "";
		
		var rows = this.table.rows;
		for (var i=0; i<num; i++) {
			str = (flag != true) ? str + def  : str + "," + def;
		}
		for (var j=1; j<rows.length; j++) {
			var key_value = this.getCell(rows[j], key);
			var cols_value = this.getCell(rows[j], cols);
			if (!key_value) alert("column " + key + " not found");
			if (!cols_value) alert("column " + cols + " not found");
			str = str.substring(0, key_value.innerText - 1) + cols_value.innerText + str.substring(key_value.innerText, num);
		}
		
		return str;
	}
	/** encode table */
	this.encodeTable = function(headstr) {
		var str = "";
		var rowcount = 0;

		var rows = this.table.rows;
		var hcells = this.header.cells;
		var encodehead = headstr.split(",");

		for (var i=1; i<rows.length; i++) {
			if (this.getCell(rows[i], "X_TAG").innerText == "") continue;

			var row = rows[i];
			for (var j=0; j<encodehead.length; j++) {
				var cell = this.getCell(row, encodehead[j]);
				if (cell == null) alert("column " + encodehead[j] + " not found！");

				str += getStrByPadLength(cell.innerText, true);
			}

			rowcount ++;
		}
		if(rowcount > 0) this.isChanged = true;
		return this.tableName.substr(0, 4) + getStrByPadPrefix(rowcount) + getStrByPadPrefix(encodehead.length, 3) + str;
	}
	
	/** get row num */
	this.getRowNum = function () {
		return this.table.rows.length;
	}
	
	/** move row */
	this.moveRow = function (index1, index2) {
		if (this.rowIndex == null || this.rowIndex == 0)
			return ;
			
		var rows = this.table.rows;
		if (index2 <=0 || index2 >= rows.length) return ;
		
		var row1 = rows[index1].cells;
		var row2 = rows[index2].cells;
		
		for (var i=0; i<row1.length; i++) {
			var temp = row1[i].innerHTML;
			row1[i].innerHTML = row2[i].innerHTML;
			row2[i].innerHTML = temp;
		}
		this.rowIndex = index2;
		this.table.rows[index1].className = "";
		this.table.rows[index2].className = "row_select";
		this.isSelected = true;
	}
	
	/** move up */
	this.moveUp = function () {
		this.moveRow(this.rowIndex, this.rowIndex-1);
	}
	
	/** move down */
	this.moveDown = function () {
		this.moveRow(this.rowIndex, this.rowIndex+1);
	}
	
	/** move first */
	this.moveFirst = function () {
		this.moveRow(this.rowIndex, 1);
	}
	
	/** move last */
	this.moveLast = function () {
		this.moveRow(this.rowIndex, this.getRowNum()-1);
	}
}

/********************************************************************************************
* HTML Print
* setNeedBorder:是否包含边框，默认包含边框
* printRow("table");打印指定表格原始内容
* print("table"),打印表格所有内容
* print("table", 'CODE,TYPE'),打印表格指定列所有内容（不需要打印的列名放入第二个参数，对应的td id="col_CODE"等，需要加上col_）
* print("table", 'CODE,TYPE', '100,200'),打印表格指定列所有/指定内容，并指定列宽
* print("table", 'CODE,TYPE', '100,200', true),打印表格列，其中参数四表示是否删除/保留列（为true表示删除参数二指定的列，为false表示只显示参数二指定的列，默认为false:保留）
* print("table", 'CODE,TYPE', '100,200', true, true),打印表格所有/指定内容,参数五可以指定是否去掉表头，默认为不去
* print("table", 'CODE', '100,200', true, true, title),打印表格所有/指定内容,参数6是加上标题
* print('table_list', 'TYPE', '100,200', true, true, '打印标题', 'SUPERUSR 2005-06-02 12:12:12'); 打印表格所有/指定内容，参数7 指定标题下面的信息
* @参数一：表名
* @参数二：列名，多个用,号分开
* @参数三：列宽，与列名对应
* @参数四：列操作类型，false|true，false表示参数二为要删除的列名，true表示参数二为要显示的列名，默认为显示列名(false)
* @参数五：是否去掉表头,默认false
* @参数五：是否去掉表头,默认false
* @参数六：标题内容
* @参数七：页尾内容
*/

/** html print */
function HtmlPrint() {
	this.hkey_root = "HKEY_CURRENT_USER";
	this.hkey_path = "\\Software\\Microsoft\\Internet Explorer\\PageSetup\\";
	this.need_border = true;
	this.printframe = getFrame("printframe");
	
	/** clear header and footer  */
	this.clearHeaderAndFooter = function() {
		this.setHeaderAndFooter("", "");
	}
	/** set default header and footer */
	this.setHeaderAndFooter = function() {
		this.setHeaderAndFooter("&w&b页码，&p/&P", "&u&b&d");
	}
	/** set header and footer */
	this.setHeaderAndFooter = function(header, footer) {
		try {
			var regwsh = new ActiveXObject("WScript.Shell");
			var hkey_key = "header";    
			regwsh.RegWrite(this.hkey_root + this.hkey_path + hkey_key, header);
			hkey_key = "footer";
			regwsh.RegWrite(this.hkey_root + this.hkey_path + hkey_key, footer);
		} catch (e) {
		}
	}
	/** set need border */
	this.setNeedBorder = function (need_border) {
		this.need_border = need_border;
	}
	/** get print object */
	this.getPrintObject = function (objname) {
		this.clearHeaderAndFooter();
		
		this.printframe.document.body.innerHTML = "";
		this.printframe.document.body.insertAdjacentHTML("afterBegin", "");
		this.printframe.document.body.insertAdjacentHTML("beforeEnd", document.getElementById(objname).outerHTML);
	
		return this.printframe.document.getElementById(objname);
	}
	/** print raw */
	this.printRaw = function (objname) {
		if (window.confirm("确定打印吗？")) {
			var newobj = this.getPrintObject(objname);
			newobj.style.display = "";
			if (this.need_border) {
				newobj.style.borderCollapse = "collapse";
				newobj.borderColor = "#000000";
			}
			newobj.bgColor = "#FFFFFF";
			newobj.style.fontSize = "12px";
						
			this.printframe.focus();
			this.printframe.print();
		}
	}
	/** 
	 * print table
	 * @param objname<table name>
	 * @param encode<table column>
	 * @param isdelete<is delete column(default false)>
	 * @param removehead<remove header>
	 * @param title
	 * @param info
	 * @param footer
	 */
	this.print = function (objname, encodehead, encodewidth, isdelete, removehead, title, info, footer) {
		if (window.confirm("确定打印吗？")) {
			var newobj = this.getPrintObject(objname);
			newobj.style.display = "";
			if (this.need_border) {
				newobj.border = "1";
				newobj.style.borderCollapse = "collapse";
			}
			newobj.borderColor = "#000000";
			newobj.bgColor = "#FFFFFF";
			newobj.style.fontSize = "12px";
			
			var header = newobj.rows[0];
			header.style.textAlign = "center";
			
			var hcells = header.cells;
			var celllength = hcells.length;
			var headlength = encodehead && encodehead != "" ? encodehead.split(",").length : 0;
			var colspan = isdelete ? celllength - headlength : headlength != 0 ? headlength : celllength;
	
			if (encodehead && encodehead != "") {
				var rows = newobj.rows;
				var heads = encodehead.split(",");
				var widths = encodewidth && encodewidth != '' ? encodewidth.split(",") : null;
				for (var i=0; i<rows.length; i++) {
					var row = rows[i];
					if (isdelete) {
						for (var j=0; j<heads.length; j++) {
							var cells = row.cells;
							for (var k=0; k<cells.length; k++) {
								var cell = cells[k];
								cell.id = hcells[k].id;
								if (cell.id == "col_" + heads[j]) {
									cell.style.display = "none";
									break;
								}
							}
						}
					} else {
						var cells = row.cells;
						for (var j=0; j<cells.length; j++) {
							var cell = cells[j];
							cell.id = hcells[j].id;
							var width = cell.width;
							var isexist = false;
							for (var k=0; k<heads.length; k++) {
								if (cell.id == "col_" + heads[k]) {
									isexist = true;
									if (widths && widths[k] != "") width = widths[k];
									break;
								}
							}
							if (!isexist) {
								cell.style.display = "none";
							} else {
								cell.width = width;
							}
						}
					}
				}
			}
		
			if (removehead) newobj.deleteRow(0);
			if (info && info != '') {
				var inforow = newobj.insertRow(0);
				var infocell = inforow.insertCell(0);
				infocell.innerHTML = info;
				infocell.colSpan = colspan;
			}
			if (title && title != '') {
				var titlerow = newobj.insertRow(0);
				var titlecell = titlerow.insertCell(0);
				titlecell.innerHTML = title;
				titlecell.colSpan = colspan;
				titlecell.align = "center";
				titlecell.height = "20";
				titlecell.style.fontSize = "20px";
				titlecell.style.fontWeight = "bold";
			}
			
			if (footer) {
				var footerrow = newobj.insertRow(newobj.rows.length);
				var footercell = footerrow.insertCell(0);
				footercell.innerHTML = footer;
				footercell.colSpan = colspan;
				footercell.height = "100";
			}
			
			this.printframe.focus();
			this.printframe.print();
		}
	}
}
/** print raw */
function printRaw(objname) {
	new HtmlPrint().printRaw(objname);
}
/** print */
function print(objname, encodehead, encodewidth, isdelete, removehead, title, info, footer) {
	new HtmlPrint().print(objname, encodehead, encodewidth, isdelete, removehead, title, info, footer);
}

/** get file name */
function getFileName(full_name) {
	full_name = full_name.replace('\\', '/');
	var index = full_name.lastIndexOf("/");
	return index == -1 ? full_name : full_name.substring(index + 1, full_name.length);	
}
/** get file image */
function getFileImage(filename) {
	var type = filename.substring(filename.lastIndexOf(".") + 1).toUpperCase();
	var image = "unknow.gif";
	switch (type) {
		case "TXT":
			image = "txt.gif"
			break;
		case "CHM":
		case "HLP":
			image = "hlp.gif"
			break;
		case "DOC":
			image = "doc.gif"
			break;
		case "PDF":
			image = "pdf.gif"
			break;
		case "MDB":
			image = "mdb.gif"
			break;
		case "GIF":
		case "JPG":
		case "PNG":
		case "BMP":
			image = "pic.gif"
			break;
		case "ASP":
		case "JSP":
		case "JS":
		case "PHP":
		case "PHP3":
		case "ASPX":
			image = "code.gif"
			break;
		case "HTM":
		case "HTML":
		case "SHTML":
			image = "htm.gif"
			break;
		case "ZIP":
		case "RAR":
			image = "zip.gif"
			break;
		case "EXE":
			image = "exe.gif"
			break;
		case "AVI":
		case "MPG":
		case "MPEG":
		case "ASF":
			image = "mp.gif"
			break;
		case "RA":
		case "RM":
			image = "rm.gif"
			break;
		case "MID":
		case "WAV":
		case "MP3":
		case "MIDI":
			image = "audio.gif"
			break;
		case "XLS":
			image = "xls.gif"
			break;
		case "PPT":
		case "PPS":
			image = "ppt.gif"
			break;
	}
	return "<img border=\"0\" src=\"component/images/file/" + image + "\"/>";
}

