								this.getCell(rows[i], "FILTER_STATE").innerText='未选择';
							
							}else if(this.getCell(rows[i], "FILTER_TAG").firstChild.checked == true)
							{
								this.getCell(rows[i], "FILTER_STATE").innerText='选择';
							}
							else{
