/** deprecated begin */
/** starts with */
function startsWith(str1, str2) {
	return str1.startsWith(str2);
}
/** trim str */
function trim(str) {
	if(typeof str =='string'){
		return str.trim();
	}else{
		return str;
	}
}
/** get request parameter by name **/
function getReqParamByName(url, name) {
	new RegExp("(^|&)"+name+"=([^&]*)").exec(decodeURIComponent(url));
	return RegExp.$2
}
