ajaxSubmit(this, 'printMTD',param, 'refeshFlag');
    if($('cond_DL_NAME').value.trim() == ""&&$('cond_DL_SNUMBER').value.trim() == ""&&$('data_DL_ZJ').value.trim() == ""&&$('cond_DL_NUMBER').value.trim() == "") {
			var show=1;
		}else{
		var show=0;
		}
		if($('cond_SHOWLIST').value.trim() == 0) 
		{
			var showlist=1;
		}
		else
		{
		var showlist=0;
		}
