    if(''==acct_id)
	{
		alert('请先查询用户信息！谢谢！');
		endPageLoading();
		return false;
	}
	var rescode = $("data_RESOURCE_CODE").value;
	var resZK = $("data_RESOURCE_ZK").value;	 
	if($('data_RESOURCE_TAG').value.trim() == "") {
		alert("请选择需要办理的资源包！");
